<?phprequire($_SERVER['DOCUMENT_ROOT'] . '/wp-load.php');$message = '<html><body>';if (isset($_POST['name']) && $_POST['name'] != '') {    $message .= '<p>Имя: <strong>' . $_POST['name'] . '</strong></p>';}if (isset($_POST['phone']) && $_POST['phone'] != '') {    $message .= '<p>Номер телефона: <strong>' . $_POST['phone'] . '</strong></p>';}if (isset($_POST['message']) && $_POST['message'] != '') {    $message .= '<p>Сообщение:</p> <p>' . $_POST['user_message'] . '</p>';}$message .= '</body></html>';if (send_mail('get_option(\'admin_email\')', $message)) echo 'ok';function send_mail($to, $message){    $subject = 'Новая заявка с сайта';    $headers = "MIME-Version: 1.0\r\n";    $headers .= "Content-Type: text/html; charset=UTF-8\r\n";    return wp_mail($to, $subject, $message, $headers);}